<script>
    export default {
        props: [
            'authUser',
        ],
        methods: {
            goHome() {
                if(this.authUser) {
                    if(window.location.pathname === `/profile/${ this.authUser.uuid }`) return;
                    this.$router.push(`/profile/${ this.authUser.uuid }`);
                }
            }
        }
    }
</script>

